  position: absolute;
  right: 10px;
  top: 50%;
  transform: translateY(-50%);
  cursor: pointer;
  transition: all ease 0.3s;
  background-image: url('data:image/png;base64,');

  height: 24px;
  width: 24px;
  background-size: contain;
}

.pwned-password-icon.show-password {
  background-image: url('data:image/png;base64,');
}

.pwned-password-input {
  padding-right: 45px;
}
